$(function(){$.fn.selectText=function(a){return $(this).each(function(c,d){if(document.selection){var b=document.body.createTextRange();b.moveToElementText(d);b.select().createTextRange();document.execCommand("Copy");if(a){document.selection.empty()}}else{if(window.getSelection){var b=document.createRange();b.selectNode(d);window.getSelection().removeAllRanges();window.getSelection().addRange(b);document.execCommand("Copy");if(a){window.getSelection().removeAllRanges()}}}})};$("#short").click(function(b){b.preventDefault();var a=$("#citation");a.find(".cite_a").each(function(){var d=$(this).text().trim(),f="";var c=d.split(" ");for(var e=0;e<c.length;++e){if(e!=c.length-1){f+=c[e][0]}else{f+=" "+c[e]}}$(this).text(f)});a.find(".cite_p").each(function(){var c=$(this).text().trim(),d=c;$.each({Proceedings:"Proc.",International:"Int'l",Conference:"Conf."},function(f,e){d=d.replace(f,e)});$(this).text(d)});a.find(".cite_s").each(function(){var c=$(this).text().trim(),d=c;d=d.replace("pages","pp.");$(this).text(d)});a.find(".cite_l").hide();a.find(".cite_m").hide();$(this).prop("disabled",true)});$("#citation > span").dblclick(function(){$(this).selectText()});$("#bibtex pre").each(function(){}).dblclick(function(){$(this).selectText()});$("#copy").click(function(a){$(this).parent().next().selectText()});$("span[itemprop=author]").click(function(b){var a=$(this).text().trim();window.open("{{ SITEURL }}/search.html?q="+a,"_blank")});$('[data-toggle="tooltip"]').tooltip()});